CREATE PROCEDURE [dbo].[usp_ConceptRank_Insert]
	@Key char(16) OUTPUT,
	@DomainKey char(16),
	@ItemName varchar(100),
	@SortOrder int = NULL,
	@Abbreviation varchar(10),
	@ColorR tinyint,
	@ColorG tinyint,
	@ColorB tinyint,
	@SessionID char(16),
	@SystemSuppliedData bit = NULL
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF

	EXECUTE spNextKey 'Concept_Rank', @Key OUTPUT

	BEGIN TRANSACTION

		/*-------------------------------------------------------------*\
		  Insert in Concept_Rank.
		\*-------------------------------------------------------------*/
		INSERT INTO Concept_Rank (
			Concept_Rank_Key,
			Domain_Key,
			Item_Name,
			Sort_Order,
			Abbreviation,
			Color_R,
			Color_G,
			Color_B,
			Entered_Session_ID,
			System_Supplied_Data			
		) VALUES (
			@Key,
			@DomainKey,
			@ItemName,
			@SortOrder,
			@Abbreviation,
			@ColorR,
			@ColorG,
			@ColorB,
			@SessionID,
			IsNull(@SystemSuppliedData, 0)		
		)
		IF @@Error <> 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

