CREATE PROCEDURE [dbo].[usp_ConceptsLinkedToTermCount_Get]
	@Count int OUTPUT,
	@ConceptKey char(16)
AS
	SELECT 		@Count = COUNT(DISTINCT C2.Concept_Key)
	FROM		Concept AS C1
