/*===========================================================================*\
  Description: Returns a concept's term and authority, with or without common name

  Parameters:	@ConceptKey	
							@IncludeCommonName

  Created:	August 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_ConceptTermAndAuthor_Get]
	@ConceptKey varchar(100),
  @IncludeCommonName bit,
  @ConceptTermAndAuthor varchar(505) output

AS

IF @IncludeCommonName=1
  SELECT @ConceptTermAndAuthor=dbo.ufn_GetFormattedTerm(
      T.Item_Name, C.Author_Copy, T2.Item_Name, C2.Author_Copy)
  FROM Concept C
  INNER JOIN Term T on T.Term_Key=C.Term_Key
  LEFT JOIN Concept C2 on C2.Meaning_Key=C.Meaning_Key
      AND C2.Preferred=1
      AND C2.Name_Type_Concept_Key='SYSTEM000000000L'
  LEFT JOIN Term T2 on T2.Term_Key = C2.Term_Key
  WHERE C.Concept_Key = @ConceptKey
ELSE
  SELECT @ConceptTermAndAuthor=dbo.ufn_GetFormattedTerm(
      T.Item_Name, C.Author_Copy, null, null)
  FROM Concept C
  INNER JOIN Term T on T.Term_Key=C.Term_Key
  WHERE C.Concept_Key = @ConceptKey

GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_ConceptTermAndAuthor_Get') AND SysStat & 0xf = 4)
BEGIN
