			Details = @Details,
			Changed_Session_ID = @SessionID

		WHERE	Conservation_Check_Key = @Key
		AND	(@Timestamp = Timestamp)

		IF @@Error <> 0 OR @@Rowcount = 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
