CREATE PROCEDURE [dbo].[usp_Domain_Insert]
	@Key char(16) OUTPUT,
	@ItemName varchar(100),
	@SubjectAreaKey char(16),
	@HasOccurrences bit,
	@DefaultHierarchyRelationTypeKey char(16),
	@DomainMask int,
	@SessionID char(16),
	@SystemSuppliedData bit = 0
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF

	EXECUTE spNextKey 'Domain', @Key OUTPUT

	BEGIN TRANSACTION
	
		INSERT INTO Domain (
			Domain_Key,
			Item_Name,
			Subject_Area_Key,
			Has_Occurrences,
			Default_Hierarchy_Relation_Type_Key,
			Domain_Mask,
			Entered_Session_ID,
			System_Supplied_Data
		) VALUES (
			@Key,
			@ItemName,
			@SubjectAreaKey,
			@HasOccurrences,
			@DefaultHierarchyRelationTypeKey,
			@DomainMask,
			@SessionID,
			@SystemSuppliedData
		)
		IF @@Error <> 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

