--  CREATED:			2003-10-06
--
SET NOCOUNT ON

IF @SortOrderIndex = 0
	SELECT Enquiry_Key AS Item_Key, Display_Caption,
		dbo.ufn_GetDateFromVagueDate(Vague_Date_Start, Vague_Date_End, Vague_Date_Type) AS Hint
	FROM 
		ENQUIRY E
		INNER JOIN 
			CONCEPT C
		ON E.Enquiry_Type_Concept_Key = C.Concept_Key
		INNER JOIN 
			TERM T
		ON C.Term_Key = T.Term_Key
		LEFT JOIN
			INDIVIDUAL I
