		INNER JOIN 
			TERM T
		ON C.Term_Key = T.Term_Key
		LEFT JOIN
			INDIVIDUAL I
		ON E.Enquirer_Name_Key = I.Name_Key
	WHERE dbo.ufn_CBWrapperForDoVagueDatesOverlap(@SearchText, Vague_Date_Start, Vague_Date_End) = 1
	ORDER BY T.PlainText


