		@Response
		@SessionID
		@Timestamp

  Created:	October 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_EnquiryResponse_Update]
	@Key char(16) OUTPUT,
	@Response text,
	@SessionID char(16),
	@Timestamp timestamp
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF

	BEGIN TRANSACTION

		/*-------------------------------------------------------------*\
		  Update in Enquiry.
		\*-------------------------------------------------------------*/
		UPDATE 	Enquiry
		SET	Response = @Response, 
			Changed_Session_ID = @SessionID
		WHERE	Enquiry_Key = @Key
		--AND	(@Timestamp = Timestamp)

		IF @@Error <> 0 OR @@Rowcount = 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
