CREATE PROCEDURE [dbo].[usp_Enquiry_DomainMask_Get] 
@Key CHAR(16),
@DomainMask INT OUTPUT

AS

--  DESCRIPTION
--  Returns the domain mask of a given Enquiry
--
--  PARAMETERS
--  NAME				DESCRIPTION
--	@Key				Enquiry
--
--
