/*===========================================================================*\
  Description:	Inserts a record into the Funding table
  Parameters:	@Key
		@ParentKey 
		@FundedByNameKey,
		@VagueDateStart 
		@VagueDateEnd 
		@VagueDateType
		@Amount
		@CurrencyConceptKey 
		@Details 
		@SessionID 
		@IsMovement

  Created:	September 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_Funding_Insert]
	@Key CHAR(16) OUTPUT,
	@ParentKey CHAR(16),
	@FundedByNameKey CHAR(16),
	@VagueDateStart INT,
	@VagueDateEnd INT,
	@VagueDateType VARCHAR(2),
	@Amount MONEY,
	@CurrencyConceptKey CHAR(16),
	@Details TEXT,
	@SessionID CHAR(16),
	@IsMovement BIT
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF

	BEGIN TRANSACTION

		IF @IsMovement = 1 BEGIN	
			EXECUTE spNextKey 'Movement_Funding', @Key OUTPUT

			INSERT INTO Movement_Funding (
				Movement_Funding_Key,
				Movement_Key,
				Funded_By_Name_Key,
				Vague_Date_Start,
				Vague_Date_End,
				Vague_Date_Type,
				Amount,
				Currency_Concept_Key,
				Details,
				Entered_Session_ID
			) VALUES (
				@Key,
				@ParentKey,
				@FundedByNameKey,
				@VagueDateStart,
				@VagueDateEnd,
				@VagueDateType,
				@Amount,
				@CurrencyConceptKey,
				@Details,
				@SessionID
			)
		END ELSE BEGIN
			EXECUTE spNextKey 'Conservation_Job_Funding', @Key OUTPUT

			INSERT INTO Conservation_Job_Funding (
				Conservation_Job_Funding_Key,
				Conservation_Job_Key,
				Funded_By_Name_Key,
				Vague_Date_Start,
				Vague_Date_End,
				Vague_Date_Type,
				Amount,
				Currency_Concept_Key,
				Details,
				Entered_Session_ID
			) VALUES (
				@Key,
				@ParentKey,
				@FundedByNameKey,
				@VagueDateStart,
				@VagueDateEnd,
				@VagueDateType,
				@Amount,
				@CurrencyConceptKey,
				@Details,
				@SessionID
			)
		END

		IF @@Error <> 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
