CREATE PROCEDURE [dbo].[usp_LocalDomain_Delete]
	@Key char(16),
	@Timestamp timestamp
AS

SET NOCOUNT ON

	BEGIN TRANSACTION
	
		DELETE 
		FROM 	Local_Domain
		WHERE	Local_Domain_Key = @Key
		AND	(@Timestamp = Timestamp)

