	--Loans Data
	ELSE IF @MovementGroupType = 1
		SELECT DISTINCT M.Movement_Key AS Item_Key, M.Movement_Type, M.Number, M.Display_Caption --DISTINCT removes duplicate records due to two direction types
		FROM MOVEMENT M
			INNER JOIN
				INDIVIDUAL I
