				INDIVIDUAL I
			ON N.Name_Key = I.NAME_KEY
				AND (dbo.ufn_GetFormattedIndividualByParams(I.Title, I.Initials, I.Forename, I.Surname) LIKE @SearchText + '%')
			LEFT JOIN
				ORGANISATION O
			ON N.Name_Key = O.NAME_KEY
				AND 
				(CASE WHEN O.Acronym IS NULL THEN 
					O.Full_Name
				ELSE 
					O.Acronym + ', ' + O.Full_Name
