/*===========================================================================*\
  Description:	Delete a record from the Movement_Of_Ownership table.

  Parameters:	@Key		Movement Of Ownership key.
		@Timestamp

  Created:	December 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_Movement_Delete]
	@Key char(16),
	@Timestamp timestamp
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF

	BEGIN TRANSACTION

		DELETE		Movement_Of_Material_Exclusion 
		FROM		Movement_Of_Material_Exclusion AS MOME
		INNER JOIN	Movement_Of_Material AS MOM ON MOM.Movement_Of_Material_Key = MOME.Movement_Of_Material_Key
		INNER JOIN	Movement_Direction AS MD ON MD.Movement_Direction_Key = MOM.Movement_Direction_Key
		INNER JOIN	Movement AS M ON M.Movement_Key = MD.Movement_Key
		WHERE		M.Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Of_Material 
		FROM		Movement_Of_Material AS MOM
		INNER JOIN	Movement_Direction AS MD ON MD.Movement_Direction_Key = MOM.Movement_Direction_Key
		INNER JOIN	Movement AS M ON M.Movement_Key = MD.Movement_Key
		WHERE		M.Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Of_Ownership_Exclusion 
		FROM		Movement_Of_Ownership_Exclusion AS MOOE
		INNER JOIN	Movement_Of_Ownership AS MOO ON MOO.Movement_Of_Ownership_Key = MOOE.Movement_Of_Ownership_Key
		INNER JOIN	Movement_Direction AS MD ON MD.Movement_Direction_Key = MOO.Movement_Direction_Key
		INNER JOIN	Movement AS M ON M.Movement_Key = MD.Movement_Key
		WHERE		M.Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Of_Ownership 
		FROM		Movement_Of_Ownership AS MOO
		INNER JOIN	Movement_Direction AS MD ON MD.Movement_Direction_Key = MOO.Movement_Direction_Key
		INNER JOIN	Movement AS M ON M.Movement_Key = MD.Movement_Key
		WHERE		M.Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Collection_Unit
		FROM		Movement_Collection_Unit AS MCU
		INNER JOIN	Movement_Direction AS MD ON MD.Movement_Direction_Key = MCU.Movement_Direction_Key
		INNER JOIN	Movement AS M ON M.Movement_Key = MD.Movement_Key
		WHERE		M.Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Direction
		FROM		Movement_Direction
		WHERE		Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Funding
		FROM		Movement_Funding
		WHERE		Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Communication
		FROM		Movement_Communication
		WHERE		Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Valuation
		FROM		Movement_Valuation
		WHERE		Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Conservation_Check
		FROM		Movement_Conservation_Check
		WHERE		Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE		Movement_Enquiry
		FROM		Movement_Enquiry
		WHERE		Movement_Key = @Key

		IF @@Error <> 0 GOTO RollbackAndExit

		DELETE 		Movement
		WHERE		Movement_Key = @Key
		AND		@Timestamp = Timestamp

		IF @@Error <> 0 OR @@Rowcount = 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_Movement_Delete') AND SysStat & 0xf = 4)
BEGIN
    	PRINT 'Setting up security on procedure usp_Movement_Delete'
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_Administrator')
		GRANT EXECUTE ON dbo.usp_Movement_Delete TO [R2k_Administrator]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_FullEdit')
		GRANT EXECUTE ON dbo.usp_Movement_Delete TO [R2k_FullEdit]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_RecordCardsOnly')
		GRANT EXECUTE ON dbo.usp_Movement_Delete TO [R2k_RecordCardsOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'Dev - JNCC SQL')
        	GRANT EXECUTE ON dbo.usp_Movement_Delete TO [Dev - JNCC SQL]
END
GO

If EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID(N'[dbo].[usp_Movement_DomainMask_Get]') AND OBJECTPROPERTY(Id, N'IsProcedure') = 1)
DROP PROCEDURE [dbo].[usp_Movement_DomainMask_Get]
GO

CREATE PROCEDURE [dbo].[usp_Movement_DomainMask_Get] 
@Key CHAR(16),
@DomainMask INT OUTPUT

AS

--  DESCRIPTION
--  Returns the domain mask of a given movement
--
--  PARAMETERS
--  NAME				DESCRIPTION
--	@Key				Movement_Key
--
--
--  AUTHOR:     		Ben Collier, Dorset Software
--  CREATED:    		03/02/2004
--

SET NOCOUNT ON


DECLARE @ItemDomain INT

SET @DomainMask = 0

DECLARE csr CURSOR FOR
SELECT DISTINCT CU.Domain_Mask
FROM Movement M
INNER JOIN Movement_Direction MD
ON M.Movement_Key = MD.Movement_Key 
LEFT JOIN Movement_Collection_Unit MCU
ON MD.Movement_Direction_Key = MCU.Movement_Direction_Key
LEFT JOIN Collection_Unit CU
ON MCU.Collection_Unit_Key = CU.Collection_Unit_Key
WHERE M.Movement_Key = @Key

OPEN csr

WHILE 1=1
BEGIN
	FETCH NEXT FROM csr INTO @ItemDomain
	IF @@FETCH_STATUS<>0
		BREAK
	SET @DomainMask = @DomainMask | @ItemDomain
END

CLOSE csr
DEALLOCATE csr

--If no CollectionUnits are linked then return 0, an unset domain
SELECT @DomainMask = ISNULL(@DomainMask, 0)

GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_Movement_DomainMask_Get') AND SysStat & 0xf = 4)
BEGIN
    	PRINT 'Setting up security on procedure usp_Movement_DomainMask_Get'
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_AddOnly')
        	GRANT EXECUTE ON dbo.usp_Movement_DomainMask_Get TO [R2k_AddOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_Administrator')
		GRANT EXECUTE ON dbo.usp_Movement_DomainMask_Get TO [R2k_Administrator]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_FullEdit')
		GRANT EXECUTE ON dbo.usp_Movement_DomainMask_Get TO [R2k_FullEdit]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_ReadOnly')
		GRANT EXECUTE ON dbo.usp_Movement_DomainMask_Get TO [R2k_ReadOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_RecordCardsOnly')
		GRANT EXECUTE ON dbo.usp_Movement_DomainMask_Get TO [R2k_RecordCardsOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'Dev - JNCC SQL')
        	GRANT EXECUTE ON dbo.usp_Movement_DomainMask_Get TO [Dev - JNCC SQL]
END

GO

