begin tran

Delete from QE_Data_Item 
where QE_Data_Row_key in 
(Select QE_Data_Row_Key from QE_Data_Row 
where QE_Session_Key = @QESessionKey)

Delete from QE_Data_Row
where QE_Session_Key = @QESessionKey

Delete from QE_Session
where QE_Session_Key = @QESessionKey
and timestamp = @Timestamp

commit tran

