/*===========================================================================*\
  Description:	

  Parameters:	

  Created:	August 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/

CREATE PROCEDURE [dbo].[usp_QETemplateField_Insert]
	@Key as char(16) output,
	@QETemplateKey as char(16),
	@QEFieldKey as char(16) = null,
	@GeneralTab as bit,
	@SpecimenTab as bit,
	@ItemName as varchar(100),
	@DefaultValue as varchar(200),
	@DefaultDisplay as varchar(200),
	@SessionID as varchar(16),
	@IsMeasurement as bit,
	@MeasurementAppliesTo as Varchar(50) = null,
	@MeasurementMethodConceptKey as char(16) = null,
	@MeasurementDuration as Varchar(50) = null,
	@MeasurementAccuracy as varchar(50) = null,
	@MeasurementParameterConceptKey as Char(16) = null,
	@MeasurementunitConceptKey as Char(16) = null,
	@MeasurementIsSpecimen as bit = null,
	@Sequence as int = 0
AS

Set Nocount off

exec spNextKey 'QE_Template_Field', @Key output

insert into QE_Template_Field
	(QE_Template_Field_Key,
	QE_Template_Key,
	QE_Field_Key,
	General_Tab,
	Specimen_Tab,
	Item_Name,
	Default_Value,
	Default_Display,
	Entered_Session_ID,
	System_Supplied_Data, Is_Measurement,
	Measurement_Applies_To,
