CREATE PROCEDURE [dbo].[usp_SpecimenLabel_Insert]
	@Key char(16) OUTPUT,
	@CollectionUnitKey char(16),
	@IsInscription bit,
	@Position varchar(100),
	@Inscription nText,
	@Translated text,
	@LanguageConceptKey varchar(4),
	@InferredAuthor tinyint,
	@Comments text,
	@AuthorNameKey char(16) = NULL,
	@ConfidenceConceptKey char(16),
	@SessionID char(16)
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF
	
	EXECUTE spNextKey 'Specimen_Label', @Key OUTPUT

	BEGIN TRANSACTION	
		INSERT INTO Specimen_Label (
			Specimen_Label_Key,
			Collection_Unit_Key,
			Is_Inscription,
			Label_Position,
			Inscription,
			Translated,
			Translated_Language_Key,
			Inferred_Author,
			Comments,
			Author_Name_Key,
			Confidence_Concept_Key,
			Entered_Session_ID
		) VALUES (
			@Key,
			@CollectionUnitKey,
			@IsInscription,
			@Position,
			@Inscription,
			@Translated,
			@LanguageConceptKey,
			IsNull(@InferredAuthor, 0),
			@Comments,
			@AuthorNameKey,
			@ConfidenceConceptKey,
			@SessionID
		)

		IF @@Error <> 0 GOTO RollBackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit:
	ROLLBACK TRANSACTION
