/*===========================================================================*\
  Description:	Returns a Specimen Label record.

  Parameters:	@Key	Specimen Label key

  Created:	Setember 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_SpecimenLabel_Select]
	@Key char(16)
AS
	SET NOCOUNT ON
	SET ANSI_NULLS ON
	SET ANSI_PADDING ON
	SET ANSI_WARNINGS ON
	SET ARITHABORT ON
	SET CONCAT_NULL_YIELDS_NULL ON
	SET QUOTED_IDENTIFIER ON
	SET NO_BROWSETABLE OFF

	SELECT		
			S.Specimen_Label_Key,
			S.Collection_Unit_Key,
			S.Is_Inscription,
			S.Label_Position,
			S.Inscription,
			S.Translated,
			S.Translated_Language_Key,
			L.Language_Key + ' - ' + L.Item_Name AS Language,
			S.Comments,
			S.Author_Name_Key,
			dbo.ufn_GetFormattedName(S.Author_Name_Key) AS AuthorName,
			S.Inferred_Author,
			S.Confidence_Concept_Key,
			TConfidence.Item_Name AS Confidence_Name,
			S.Entered_Session_ID,
			S.Changed_Session_ID,
			S.Custodian,
			S.[Timestamp]
	FROM		Specimen_Label AS S
	LEFT JOIN 	Language AS L ON L.Language_Key = S.Translated_Language_Key 
	LEFT JOIN	VW_ConceptTerm AS TConfidence ON TConfidence.Concept_Key = S.Confidence_Concept_Key
	WHERE 		Specimen_Label_Key = @Key

SET NOCOUNT OFF
GO

