	[Life_Sciences] [bit] NULL,
	[Number] [varchar] (30) COLLATE SQL_Latin1_General_CP1_CI_AS NULL,
	[Hint] [nvarchar] (150) COLLATE SQL_Latin1_General_CP1_CI_AS NULL 
