CREATE PROCEDURE [dbo].[usp_SurveyEvent_Insert]
	@Key char(16) OUTPUT,
	@VagueDateStart int,
	@VagueDateEnd int,
	@VagueDateType varchar(2) = NULL,
	@SpatialRef varchar(40),
	@SpatialRefSystem varchar(4),
	@Lat float,
	@Long float,
	@SpatialRefQualifier varchar(20),
	@LocationKey char(16),
	@SurveyKey char(16),
	@Comment text,
	@EnteredBy char(16),
	@LocationName varchar(100)
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF
	
	EXECUTE spNextKey 'Survey_Event', @Key OUTPUT

	IF @SpatialRef IS NULL 
		SELECT 
			@SpatialRef=Spatial_Ref,
			@SpatialRefSystem=Spatial_Ref_System,
			@SpatialRefQualifier=Spatial_Ref_Qualifier,
			@Lat=Lat,
			@Long=Long
		FROM Location
		WHERE Location_Key=@LocationKey

	BEGIN TRANSACTION	
		INSERT INTO Survey_Event (
			Survey_Event_Key,
			Vague_Date_Start,
			Vague_Date_End,
			Vague_Date_Type,
			Spatial_Ref,
			Spatial_Ref_System,
			Lat,
			Long,
			Spatial_Ref_Qualifier,
			Location_Key,
			Survey_Key,
			Comment,
			Entered_By,
			Location_Name
		) VALUES (
			@Key,
			@VagueDateStart,
			@VagueDateEnd,
			IsNull(@VagueDateType, 'U'),
			@SpatialRef,
			@SpatialRefSystem,
			@Lat,
			@Long,
			@SpatialRefQualifier,
			@LocationKey,
			@SurveyKey,
			@Comment,
			@EnteredBy,
			@LocationName
		)

		IF @@Error <> 0 GOTO RollBackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit:
	ROLLBACK TRANSACTION
GO

