CREATE PROCEDURE [dbo].[usp_Task_DomainMask_Get] 
@Key CHAR(16),
@DomainMask INT OUTPUT

AS

--  DESCRIPTION
--  Returns the domain mask of a given Task
--
--  PARAMETERS
--  NAME				DESCRIPTION
--	@Key				Conservation_Task_Key
--
--
--  AUTHOR:     		Ben Collier, Dorset Software
