--

SET NOCOUNT ON

SELECT @DomainMask = Domain_Mask
FROM Conservation_Task CT
INNER JOIN Conservation_Check CC ON CT.Conservation_Check_Key = CC.Conservation_Check_Key
WHERE CT.Conservation_Task_Key = @Key

