  Drop stored proc before re-creating.
\*===========================================================================*/
IF EXISTS (SELECT *
	   FROM   SysObjects 
	   WHERE  Id = Object_Id(N'[dbo].[usp_TaxonDetermination_Delete]')
	   AND 	  Type = 'P')
    DROP PROCEDURE [dbo].[usp_TaxonDetermination_Delete]
GO

/*===========================================================================*\
  Description:	Delete a record in the Taxon Determination table.
		Ensures the Domain mask of the specimen is also updated.

  Parameters:	@DeterminationKey
		@IsForSpecimen

  Created:	November 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_TaxonDetermination_Delete]
	@DeterminationKey char(16),
	@IsForSpecimen bit,
	@Timestamp timestamp
AS
	DECLARE @SpecimenKey char(16),
		@ConceptKey char(16),
		@ConceptMask bigint,
		@OccurrenceKey char(16),
		@WasPreferredForOccurrence bit,
		@WasPreferredForSpecimen bit

	SET @WasPreferredForSpecimen = 0
	SET @WasPreferredForOccurrence = 0

	BEGIN TRANSACTION
		/*-------------------------------------------------------------*\
		  Set some values to search for new preferred, if deleting 
		  current preferred.
		\*-------------------------------------------------------------*/
		-- Check for Specimen.
		IF EXISTS(SELECT * FROM Specimen_Unit WHERE Preferred_Taxon_Determination_Key = @DeterminationKey) BEGIN
			SELECT	@WasPreferredForSpecimen = 1,
				@SpecimenKey = Collection_Unit_Key
			FROM	Specimen_Unit
			WHERE	Preferred_Taxon_Determination_Key = @DeterminationKey

			-- Need to clear the field because of referential integrity.
			UPDATE 	Specimen_Unit
			SET	Preferred_Taxon_Determination_Key = NULL
			WHERE	Collection_Unit_Key = @SpecimenKey
			IF @@Error <> 0 GOTO RollbackAndExit
		END

		-- Check for Occurrence.
		SELECT		@ConceptKey = TDM.Concept_Key,
				@OccurrenceKey = TD.Taxon_Occurrence_Key,
				@WasPreferredForOccurrence = TD.Preferred,
				@SpecimenKey = TD.Specimen_Collection_Unit_Key
		FROM		Taxon_Determination TD
		INNER JOIN 	Taxon_Dictionary_Concept_Mapping TDM ON TDM.Taxon_List_Item_Key = TD.Taxon_List_Item_Key
		WHERE		TD.Taxon_Determination_Key = @DeterminationKey

		/*-------------------------------------------------------------*\
		  Delete either from Determination table, or just the link.
		\*-------------------------------------------------------------*/
		IF @OccurrenceKey IS NOT NULL AND @SpecimenKey IS NOT NULL 
			-- Determination linked to both a specimen AND an occurrence
			IF @IsForSpecimen = 1
				-- Deleting for specimen, clear the link and keep Occurrence key
				UPDATE	Taxon_Determination
				SET	Specimen_Collection_Unit_Key = NULL
				WHERE	Taxon_Determination_Key = @DeterminationKey
				AND	(@Timestamp = Timestamp)
			ELSE
				-- Deleting for occurrence, clear the link and keep Spceimen key
				UPDATE	Taxon_Determination
				SET	Taxon_Occurrence_Key = NULL
				WHERE	Taxon_Determination_Key = @DeterminationKey
				AND	(@Timestamp = Timestamp)
		ELSE
			-- Only either specimen or occurrence key, safe to delete.
			DELETE	Taxon_Determination
			WHERE	Taxon_Determination_Key = @DeterminationKey
			AND	(@Timestamp = Timestamp)

		IF @@Error <> 0 OR @@Rowcount = 0 GOTO RollbackAndExit

		/*-------------------------------------------------------------*\
		  See if preferred flags need to be updated, and where.
		\*-------------------------------------------------------------*/
		-- Retrieve the mask of the concept from deleted determination.
		EXECUTE	usp_Get_Concept_Domain_Mask @ConceptKey, @ConceptMask OUTPUT

		-- Need to look for a new preferred determination. Default to first one found.
		IF @WasPreferredForOccurrence = 1 OR @WasPreferredForSpecimen = 1 BEGIN
			DECLARE	@NewConceptKey char(16),
				@NewConceptMask bigint,
				@NewPreferredDeterminationKey char(16)

			-- Get new preferred first, if there are determinations left to choose from.
			IF EXISTS(SELECT * FROM Taxon_Determination WHERE Taxon_Occurrence_Key = @OccurrenceKey)
			BEGIN
				-- For occurrences, it all happens in Taxon_Determination table.
				IF @WasPreferredForOccurrence = 1 BEGIN
					SELECT	TOP 1 @NewPreferredDeterminationKey = Taxon_Determination_Key
					FROM	Taxon_Determination
					WHERE	Taxon_Occurrence_Key = @OccurrenceKey

					UPDATE 	Taxon_Determination
					SET	Preferred = 1
					WHERE	Taxon_Determination_Key = @NewPreferredDeterminationKey
					IF @@Error <> 0 GOTO RollbackAndExit
				END

				-- For specimen, it happens in Specimen_Unit table.
				IF @WasPreferredForSpecimen = 1 BEGIN
					SELECT	TOP 1 @NewPreferredDeterminationKey = Taxon_Determination_Key
					FROM	Taxon_Determination
					WHERE	Specimen_Collection_Unit_Key = @SpecimenKey

					UPDATE 	Specimen_Unit
					SET	Preferred_Taxon_Determination_Key = @NewPreferredDeterminationKey
					WHERE	Collection_Unit_Key = @SpecimenKey
					IF @@Error <> 0 GOTO RollbackAndExit
				END

				-- Get concept key of new preferred determination
				SELECT		@NewConceptKey = TDM.Concept_Key
				FROM		Taxon_Determination TD
				INNER JOIN	Taxon_Dictionary_Concept_Mapping TDM ON TDM.Taxon_List_Item_Key = TD.Taxon_List_Item_Key
				WHERE		Taxon_Determination_Key = @NewPreferredDeterminationKey
			END

			-- Retrieve the mask of the concept from deleted determination.
			EXECUTE	usp_Get_Concept_Domain_Mask @NewConceptKey, @NewConceptMask OUTPUT

			-- Different mask, so switch old one OFF and new one ON.
			IF @SpecimenKey IS NOT NULL AND @ConceptMask <> @NewConceptMask
			BEGIN
				EXECUTE	usp_CollectionUnit_Update_DomainMask @SpecimenKey, @ConceptMask, 0
				IF @@Error <> 0 GOTO RollbackAndExit

				EXECUTE	usp_CollectionUnit_Update_DomainMask @SpecimenKey, @NewConceptMask, 1
				IF @@Error <> 0 GOTO RollbackAndExit
			END
		END

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_TaxonDetermination_Delete') AND SysStat & 0xf = 4)
BEGIN
    	PRINT 'Setting up security on procedure usp_TaxonDetermination_Delete'
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_AddOnly')
        	GRANT EXECUTE ON dbo.usp_TaxonDetermination_Delete TO [R2k_AddOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_Administrator')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_Delete TO [R2k_Administrator]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_FullEdit')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_Delete TO [R2k_FullEdit]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_RecordCardsOnly')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_Delete TO [R2k_RecordCardsOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'Dev - JNCC SQL')
        	GRANT EXECUTE ON dbo.usp_TaxonDetermination_Delete TO [Dev - JNCC SQL]
END
GO

/*===========================================================================*\
  Drop stored proc before re-creating.
\*===========================================================================*/
IF EXISTS (SELECT *
	   FROM   SysObjects 
	   WHERE  Id = Object_Id(N'[dbo].[usp_TaxonDetermination_Insert]')
	   AND 	  Type = 'P')
    DROP PROCEDURE [dbo].[usp_TaxonDetermination_Insert]
GO

/*===========================================================================*\
  Description:	Inserts a record in the Taxon Determination table.
		Ensures the Domain mask of the specimen is also updated.

  Parameters:	@Key 
		@DeterminedItemKey
		@OccurrenceKey  
		@SpecimenCollectionUnitKey  
		@DeterminationTypeKey  
		@NomenclaturalStatusConceptKey 
		@Confidence
		@DeterminerNameKey 
		@InferredDeterminer 
		@DeterminerRoleKey 
		@VagueDateStart
		@VagueDateEnd 
		@VagueDateType 
		@UsedSpecimen 
		@Preferred
		@Method
		@Notes 
		@SessionID
		@IsForSpecimen		Indicates whether to update preferred 
					flag in Specimen_Unit or Determination.

  Created:	July 2003

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_TaxonDetermination_Insert]
	@Key char(16) output, 
	@DeterminedItemKey char(16), 
	@OccurrenceKey char(16), 
	@SpecimenCollectionUnitKey char(16), 
	@DeterminationTypeKey char(16), 
	@NomenclaturalStatusConceptKey char(16),
	@Confidence tinyint,
	@DeterminerNameKey char(16), 
	@InferredDeterminer tinyint,
	@DeterminerRoleKey char(16), 
	@VagueDateStart int, 
	@VagueDateEnd int, 
	@VagueDateType varchar(2),
	@UsedSpecimen bit,
	@Preferred bit,
	@Method text,
	@Notes text,
	@SessionID char(16),
	@IsForSpecimen bit
AS
	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF
	SET XACT_ABORT ON

	--Wrap everything in a transaction
	BEGIN TRANSACTION

		-- Get the user name key, as Taxon_Determination table doesn't have SessionID fields.
		DECLARE	@EnteredBy char(16),
			@PreferredForSpecimen bit

		SELECT	@EnteredBy = User_Name_Key FROM Session WHERE Session_ID = @SessionID
		SET	@PreferredForSpecimen = 0

		-- Get a new key first.
		EXECUTE spNextKey 'Taxon_Determination', @Key OUTPUT

		/*---------------------------------------------------------------------------------*\
		  Ensure only one preferred determination per occurrence.
		\*---------------------------------------------------------------------------------*/
		IF @IsForSpecimen = 1
		BEGIN
			-- Either Preferred passed in as true, or not preferred but not set in 
			-- Speciment Unit either.
			IF @Preferred = 1
			OR (@Preferred = 0 AND EXISTS ( SELECT * FROM Specimen_Unit 
							WHERE Collection_Unit_Key = @SpecimenCollectionUnitKey 
							AND Preferred_Taxon_Determination_Key IS NULL))
				SET @PreferredForSpecimen = 1

			-- Not used for Taxon_Occurrence, unless not already one present
			IF EXISTS(SELECT 1 FROM Taxon_Determination WHERE Taxon_Occurrence_Key=@OccurrenceKey AND Preferred=1)
				SET @Preferred = 0
		END ELSE BEGIN
			-- If new determination not preferred, but there isn't one already, change the flag.
			IF @Preferred = 0 
			AND NOT EXISTS(SELECT * FROM Taxon_Determination WHERE Taxon_Occurrence_Key = @OccurrenceKey AND Preferred = 1)
				SET @Preferred = 1
			ELSE
			-- If new determination is preferred, make sure previous preferred is turned off.
			IF @Preferred = 1
			BEGIN
				UPDATE	Taxon_Determination
				SET	Preferred = 0
				WHERE	Taxon_Occurrence_Key = @OccurrenceKey
				AND	Preferred = 1

				IF @@Error <> 0 GOTO RollbackAndExit
			END
		END

		/*---------------------------------------------------------------------------------*\
		  Do table insert.
		\*---------------------------------------------------------------------------------*/
		INSERT INTO Taxon_Determination (
			Taxon_Determination_Key, Taxon_List_Item_Key, Taxon_Occurrence_Key, 
			Vague_Date_Start, Vague_Date_End, Vague_Date_Type, Comment, Preferred, Determiner, 
			Determination_Type_Key, Determiner_Role_Key, Entered_By, Entry_Date, 
			Specimen_Collection_Unit_Key, Nomenclatural_Status_Concept_Key,
			Confidence, Used_Specimen, Method, Inferred_Determiner
		) VALUES (
			@Key, @DeterminedItemKey, @OccurrenceKey, 
			@VagueDateStart, @VagueDateEnd, @VagueDateType, @Notes, @Preferred, @DeterminerNameKey,
			@DeterminationTypeKey, @DeterminerRoleKey, @EnteredBy, GetDate(), 
			@SpecimenCollectionUnitKey, @NomenclaturalStatusConceptKey, 
			@Confidence, @UsedSpecimen, @Method, @InferredDeterminer
		)

		IF @@Error <> 0 GOTO RollbackAndExit

		
		IF @PreferredForSpecimen = 1
		BEGIN
			UPDATE	Specimen_Unit
			SET	Preferred_Taxon_Determination_Key = @Key
			WHERE	Collection_Unit_Key = @SpecimenCollectionUnitKey

			IF @@Error <> 0 GOTO RollbackAndExit
		END

		/*-------------------------------------------------------------*\
		  Switch bit of new mask ON in Collection_Unit.
		\*-------------------------------------------------------------*/
		IF @IsForSpecimen = 1 AND @PreferredForSpecimen = 1
		BEGIN
			DECLARE @ConceptKey char(16),
				@ConceptMask int

			-- Get the right concept before getting the mask
			SELECT	@ConceptKey = Concept_Key
			FROM	Taxon_Dictionary_Concept_Mapping
			WHERE	Taxon_List_Item_Key = @DeterminedItemKey

			-- Retrieve the mask of the new concept.
			EXECUTE	usp_Get_Concept_Domain_Mask @ConceptKey, @ConceptMask OUTPUT
			-- And switch appropriate bit ON in Collection_Unit
			EXECUTE	usp_CollectionUnit_Update_DomainMask @SpecimenCollectionUnitKey, @ConceptMask, 1

			IF @@Error <> 0 GOTO RollbackAndExit
		END

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_TaxonDetermination_Insert') AND SysStat & 0xf = 4)
BEGIN
    	PRINT 'Setting up security on procedure usp_TaxonDetermination_Insert'
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_AddOnly')
        	GRANT EXECUTE ON dbo.usp_TaxonDetermination_Insert TO [R2k_AddOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_Administrator')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_Insert TO [R2k_Administrator]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_FullEdit')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_Insert TO [R2k_FullEdit]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_RecordCardsOnly')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_Insert TO [R2k_RecordCardsOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'Dev - JNCC SQL')
        	GRANT EXECUTE ON dbo.usp_TaxonDetermination_Insert TO [Dev - JNCC SQL]
END
GO

/*===========================================================================*\
  Drop stored proc before re-creating.
\*===========================================================================*/
IF EXISTS (SELECT *
	   FROM   SysObjects 
	   WHERE  Id = Object_Id(N'[dbo].[usp_TaxonDetermination_TaxonOccurrence_Update]')
	   AND 	  Type = 'P')
    DROP PROCEDURE [dbo].[usp_TaxonDetermination_TaxonOccurrence_Update]
GO

/*===========================================================================*\


  Parameters:	@TaxonOccurrenceKey	-Taxon Occurrence Key
				@SpecimenUnitKey	-Specimen Unit Key

  Created:	Feb 2004

  Last revision information:
    $Revision: 2 $
    $Date: 2/02/09 14:35 $
    $Author: Pauldavies $

\*===========================================================================*/
CREATE PROCEDURE [dbo].[usp_TaxonDetermination_TaxonOccurrence_Update]
	@TaxonDeterminationKey char(16),
	@SpecimenUnitKey char(16)
AS

DECLARE @TaxonOccurrenceKey CHAR(16)

	-- Required to be able to get number of changed records.
	SET NOCOUNT OFF

	BEGIN TRANSACTION	
		--Update Taxon_Occurrence field on Taxon_Determination
		SELECT @TaxonOccurrenceKey = XO.Taxon_Occurrence_Key
		FROM Specimen_Unit SU
			INNER JOIN Specimen_Field_Data SFD ON SU.Collection_Unit_Key = SFD.Collection_Unit_Key
			INNER JOIN Taxon_Occurrence XO ON SFD.Taxon_Occurrence_Key = XO.Taxon_Occurrence_Key
		WHERE SU.Collection_Unit_Key = @SpecimenUnitKey
			AND SFD.Gathering_Event = 1	
		IF @@Error <> 0 GOTO RollbackAndExit


		UPDATE Taxon_Determination
			SET Taxon_Occurrence_Key = @TaxonOccurrenceKey
		WHERE Taxon_Determination_Key = @TaxonDeterminationKey
		IF @@Error <> 0 GOTO RollbackAndExit

	COMMIT TRANSACTION
	RETURN 0

RollBackAndExit: 
	ROLLBACK TRANSACTION
GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_TaxonDetermination_TaxonOccurrence_Update') AND SysStat & 0xf = 4)
BEGIN
    	PRINT 'Setting up security on procedure usp_TaxonDetermination_TaxonOccurrence_Update'
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_AddOnly')
        	GRANT EXECUTE ON dbo.usp_TaxonDetermination_TaxonOccurrence_Update TO [R2k_AddOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_Administrator')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_TaxonOccurrence_Update TO [R2k_Administrator]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_FullEdit')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_TaxonOccurrence_Update TO [R2k_FullEdit]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_ReadOnly')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_TaxonOccurrence_Update TO [R2k_ReadOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_RecordCardsOnly')
		GRANT EXECUTE ON dbo.usp_TaxonDetermination_TaxonOccurrence_Update TO [R2k_RecordCardsOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'Dev - JNCC SQL')
        	GRANT EXECUTE ON dbo.usp_TaxonDetermination_TaxonOccurrence_Update TO [Dev - JNCC SQL]
