CREATE PROCEDURE [dbo].[usp_TransferOfOwnershipDetails_Select_ForMovement] 
@ParentKey CHAR(16)

AS

--  DESCRIPTION
--  Returns Transfer Of Ownership Details for a specified movement
--
--  PARAMETERS
--  NAME				DESCRIPTION
--	@ParentKey 			When specified, only the records associated with the parent key are returned
--
--
--  AUTHOR:     		Ben Collier, Dorset Software
--  CREATED:    		2003-08-21
--
SET NOCOUNT ON

SELECT DISTINCT MO.Movement_Of_Ownership_Key AS Item_Key, Vague_Date_Start, Vague_Date_End, Vague_Date_Type
FROM
MOVEMENT M
	INNER JOIN 
		MOVEMENT_DIRECTION MD 
	ON M.Movement_Key = MD.Movement_Key 
		AND (M.Movement_Key = @ParentKey)
		AND (MD.Outbound = 1)
	INNER JOIN 
		MOVEMENT_OF_OWNERSHIP MO
	ON MD.Movement_Direction_Key = MO.Movement_Direction_Key

GO

/*===========================================================================*\
  Grant permissions.
\*===========================================================================*/
IF EXISTS (SELECT * FROM SysObjects WHERE Id = OBJECT_ID('dbo.usp_TransferOfOwnershipDetails_Select_ForMovement') AND SysStat & 0xf = 4)
BEGIN
    	PRINT 'Setting up security on procedure usp_TransferOfOwnershipDetails_Select_ForMovement'
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_AddOnly')
        	GRANT EXECUTE ON dbo.usp_TransferOfOwnershipDetails_Select_ForMovement TO [R2k_AddOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_Administrator')
		GRANT EXECUTE ON dbo.usp_TransferOfOwnershipDetails_Select_ForMovement TO [R2k_Administrator]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_FullEdit')
		GRANT EXECUTE ON dbo.usp_TransferOfOwnershipDetails_Select_ForMovement TO [R2k_FullEdit]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_ReadOnly')
		GRANT EXECUTE ON dbo.usp_TransferOfOwnershipDetails_Select_ForMovement TO [R2k_ReadOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'R2k_RecordCardsOnly')
		GRANT EXECUTE ON dbo.usp_TransferOfOwnershipDetails_Select_ForMovement TO [R2k_RecordCardsOnly]
	IF EXISTS (SELECT * FROM SYSUSERS WHERE NAME = 'Dev - JNCC SQL')
        	GRANT EXECUTE ON dbo.usp_TransferOfOwnershipDetails_Select_ForMovement TO [Dev - JNCC SQL]
END

GO

